import React, { useState, useEffect } from 'react';
import axios from 'axios';

function UserForm({ onSaved, editingUser, onCancel }) {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [phone, setPhone] = useState('');
  const [password, setPassword] = useState('');

  useEffect(() => {
    if (editingUser) {
      setName(editingUser.name || '');
      setEmail(editingUser.email || '');
      setPhone(editingUser.phone || '');
      setPassword('');
    } else {
      setName(''); setEmail(''); setPhone(''); setPassword('');
    }
  }, [editingUser]);

  const submit = async (e) => {
    e.preventDefault();
    try {
      if (editingUser) {
        await axios.put(`/users/${editingUser.id}`, { name, email, phone, password: password || undefined });
      } else {
        await axios.post('/users', { name, email, phone, password });
      }
      onSaved();
    } catch (err) {
      alert(err.response?.data?.message || 'Error');
    }
  };

  return (
    <form onSubmit={submit}>
      <div className="mb-2">
        <label className="form-label">Name</label>
        <input value={name} onChange={e=>setName(e.target.value)} className="form-control" required />
      </div>
      <div className="mb-2">
        <label className="form-label">Email</label>
        <input value={email} onChange={e=>setEmail(e.target.value)} className="form-control" type="email" required />
      </div>
      <div className="mb-2">
        <label className="form-label">Phone</label>
        <input value={phone} onChange={e=>setPhone(e.target.value)} className="form-control" />
      </div>
      <div className="mb-2">
        <label className="form-label">Password {editingUser ? <small>(leave blank to keep)</small> : ''}</label>
        <input value={password} onChange={e=>setPassword(e.target.value)} className="form-control" type="password" required={!editingUser} />
      </div>
      <div className="d-flex gap-2">
        <button className="btn btn-primary" type="submit">Save</button>
        <button className="btn btn-secondary" type="button" onClick={onCancel}>Cancel</button>
      </div>
    </form>
  );
}

export default UserForm;
